<?php
namespace hexydec\html;

class script {

	protected $content = null;

	/**
	 * Parses an array of tokens into an HTML documents
	 *
	 * @param array &$tokens An array of tokens generated by tokenise()
	 * @param array $config An array of configuration options
	 * @return bool Whether the parser was able to capture any objects
	 */
	public function parse(array &$tokens) {
		$value = '';
		$token = current($tokens);
		$quotes = 0;
		while ($token !== false && ($quotes % 2 || $token['type'] != 'tagclose' || $token['value'] != '</script>')) {
			$value .= $token['value'];

			// count quotes so we don't capture a script tag in a string
			$quotes += substr_count(str_replace(['\\\\', '\\\\"', "\\\\'"], ['', '', ''], $token['value']), '"');
			$token = next($tokens);
		}
		prev($tokens);
		if ($value) {
			$this->content = $value;
		}
	}

	public function minify(array $minify) {
		if ($minify['js'] && $this->content) {
			$this->content = $minify['js'] === true ? trim($this->content) : call_user_func($minify['js'], $this->content);
		}
	}

	public function html() {
		return $this->content;
	}

	public function __get($var) {
		return $this->$var;
	}
}
